import { reqGoodsInfo,reqAddOrUpdateShipCart} from "@/api";
const state={
    goodinfo:{},
};
const mutations={
    GETGOODINFO(state,goodinfo){
        state.goodinfo=goodinfo
    }
};
const actions={
    async getGoodInfo({commit},skuid){
        let result=await reqGoodsInfo(skuid);
        if(result.code==200){
            commit('GETGOODINFO',result.data)
        }
    },
    async addOrUpdateShipCart({commit},{skuid,skuNum}){
        let result=await reqAddOrUpdateShipCart(skuid,skuNum)
        // 服务器不返回参数，只返回操作成功
        // async如果执行，返回一个promise
        if(result.code==200){
            return 'OK'
        }else{
            // 代表加入购物车失败
        return Promise.reject(new Error('faile'))
        }
    }
};
const getters={
    // 简化数据
    categoryView(state){
        // 至少是一个空对象
        return state.goodinfo.categoryView||{};
    },
    skuInfo(state){
        return state.goodinfo.skuInfo||{};
    },
    spuSaleAttrList(state){
        return state.goodinfo.skuSaleAttrList||[];
    }
};
export default{
    state,
    mutations,
    actions,
    getters
}
