import Son from "./Son";
import Daughter from "./Daughter";
export default {
  name: "ChildrenParentTest",
  data: function data() {
    return {
      money: 1000
    };
  },
  methods: {
    //找儿子借钱
    JieQianFromXM: function JieQianFromXM(money) {
      //父组件的数据累加100
      this.money += money;
      this.$refs.xm.money -= money;
    },
    JieQianFromXH: function JieQianFromXH(money) {
      //父组件的数据累加150
      this.money += money;
      this.$refs.xh.money -= money;
    },
    JieQianAll: function JieQianAll(money) {
      this.money += 2 * money;
      this.$children.forEach(function (item) {
        return item.money -= money;
      }); //不建议用枚举获取子组件：因为没办法确定到底是那个子组件
      // this.$children[0].money -=money;
    },
    SendInfo: function SendInfo() {
      //在父组件中获取到子组件（数据+方法）
      this.$refs.xm.tinghua();
    }
  },
  components: {
    Son: Son,
    Daughter: Daughter
  }
};
