// 在这里对API接口进行统一管理
import requests from "./requests";
import mockRequest from './mockAjax'

// 三级分类接口
// /api/product/getBaseCategoryList  GET 无参数
export const reqCategoryList=()=>{
    // 发请求
   return  requests({url:"/product/getBaseCategoryList",method:"get"})
}

export const reqBannerList=()=>{
    // 发请求
   return  mockRequest({url:"/banner",method:"get"})
}

export const reqFloorList=()=>{
    // 发请求
   return  mockRequest({url:"/floor",method:"get"})
}

// 获取搜索模块接口,请求方式POST,带参数
export const reqGetSearchInfo=(params)=>{
    // 发请求
   return  requests({url:"/list",method:"post",data:params})
}

export const reqGoodsInfo=(skuid)=>{
    // 发请求
   return  requests({url:`/item/${skuid}`,method:"get"})
}

// 产品添加购物车，或者更新个数的请求
export const reqAddOrUpdateShipCart=(skuid,skuNum)=>requests
({url:`/cart/addToCart/${skuid}/${skuNum}`,method:"post"})
