// 配置路由的地方
import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter);
import Home from '@/pages/Home'
import Search from '@/pages/Search'
import Login from '@/pages/Login'
import Register from '@/pages/Register'
import Detail from '@/pages/Detail'
import AddCartSuccess from '@/pages/AddCartSuccess'

export default new VueRouter({
    scrollBehavior(to,from,savedPosition){
        return {y:0}//y=0，滚动条在最上方
    },
    routes:[
        {
            name:'home',
            path:'/home',
            component:Home,
            meta:{FooterShow:true}
            // FooterShow属性用于显示Footer组件
        },
        {
            name:'search',
            path:'/search/:keyword?',
            // params参数需要占位
            component:Search,
            meta:{FooterShow:true}
            // FooterShow属性用于显示Footer组件
        },
        {
            name:'login',
            path:'/login',
            component:Login,
            meta:{FooterShow:false}
            // FooterShow属性用于隐藏Footer组件
        },
        {
            name:'register',
            path:'/register',
            component:Register,
            meta:{FooterShow:false}
            // FooterShow属性用于隐藏Footer组件
        },
        {
            name:'detail',
            path:'/detail/:skuid',
            component:Detail,
            meta:{FooterShow:true}
        },
        {
            name:'addcartsuccess',
            path:"/addcartsuccess",
            component:AddCartSuccess,
            meta:{FooterShow:true}
        }
    ]
})
