// search模块的小仓库

import { reqGetSearchInfo } from "@/api";
const state={
    searchList:{}
};
const mutations={
    getSearchList(state,searchList){
        state.searchList=searchList
    }
};
const actions={
    // 获取search模块的数据
    async getSearchList({commit,params}){
       let result=await reqGetSearchInfo(params)
       if(result==200)
       commit("GETSEARCHLIST",result.data)
    }
};
// 主要作用，简化仓库数据
const getters={
    // goodsList:state=>state.search.searchList.goodList
    // 当前仓库的state，不是大仓库的state
    goodsList(state){
            return state.searchList.goodsListstate
            // zhe'y'x
    }
};
export default {
    state,
    mutations,
    actions,
    getters
}
